import React from 'react';

function Carousel(){

    const [slide, setSlide] = React.useState(0);

    var carImages=[
        {
            src: require("../Images/rivulet.jpg"),
            alt: "Image1",
        },
        {
            src: require("../Images/structure.png"),
            alt: "Image2",
        },
        {
            src: require("../Images/fastboy.jpg"),
            alt: "Image3",
        },
    ]

    return (
        <div id="carousel" className="flex flex-row max-w-screen max-h-[20vh]">
            <div className="absolute text-white" onClick={()=>{if(slide===0){setSlide(carImages.length-1)}else{setSlide(slide-1)}}}>left</div>
            <div className="text-white" onClick={()=>{if(slide===carImages.length-1){setSlide(0)}else{setSlide(slide+1)}}}>right</div>
            {carImages.map((img, index) => {
                return(<>
                <img className={`${slide===index ? "":"hidden"}`} src={img.src} alt={img.alt} key={index}></img>
            <div className="absolute bg-white rounded-[50%]"></div></>)
            })}
        </div>
    )
}

export default Carousel
