import React from 'react';
import Axios from 'axios';
import {getCoreRowModel, useReactTable} from '@tanstack/react-table';

function Databases(){

    //useState to store the data obtained from the database
    const [arrayData, setarrayData] = React.useState([]);
    const columns = [
        {
            accessorKey: 'firstName',
            header: "First Name",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'lastName',
            header: "Last Name",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'Email',
            header: "Email Address",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'Address',
            header: "Address",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'Phone Number',
            header: "Phone Number",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'Access Level',
            header: "Access Level",
            cell: (props) => <p>{props.getValue()}</p>
        },
        {
            accessorKey: 'Password',
            header: "Password",
            cell: (props) => <p>{props.getValue()}</p>
        },
    ]
    const table = useReactTable({
        arrayData,
        columns,
        getCoreRowModel:getCoreRowModel()
    })
    console.log(table.getHeaderGroups());

    //runs only on first load. Only used to automatically fetch the database data and store it
    React.useEffect(() => {
        try {
            Axios.get("/test").then((response) => {
                setarrayData(response.data.rows)
            })
        } catch (err){
            console.log(err);
        }
    }, [])

    return(
        <div>
            <h1>Databases</h1>
            <div className='table-container'>
                
            </div>
        </div>
    )
}

export default Databases;
