import React from 'react';

function Carousel(){

    const [slide, setSlide] = React.useState(0);

    var carImages=[
        {
            src: require("../Images/rivulet.jpg"),
            alt: "Image1",
        },
        {
            src: require("../Images/structure.png"),
            alt: "Image2",
        },
        {
            src: require("../Images/fastboy.jpg"),
            alt: "Image3",
        },
    ]

    return (
        <div id="carousel" className="flex flex-row max-w-screen max-h-[20vh]">
            <div disable={slide===0} onClick={()=>{setSlide(slide-1)}}>left</div>
            <div disable={slide===carImages.length}onClick={()=>{setSlide(slide+1)}}>right</div>
            {carImages.map((img, index) => {
                return(<img className={`${slide===index ? "":"hidden"}`} src={img.src} alt={img.alt} key={index}></img>)
            })}
        </div>
    )
}

export default Carousel
