import React from 'react';
import {ArrowRightCircleIcon, ArrowLeftCircleIcon} from "@heroicons/react/24/outline"

function Carousel(){

    const [slide, setSlide] = React.useState(0);

    var carImages=[
        {
            src: require("../Images/rivulet.jpg"),
            alt: "Image1",
        },
        {
            src: require("../Images/structure.png"),
            alt: "Image2",
        },
        {
            src: require("../Images/fastboy.jpg"),
            alt: "Image3",
        },
    ]

    return (
        <div id="carousel" className="flex flex-row max-w-screen max-h-[20vh]">
            <button className="w-5 h-10 text-white" onClick={()=>{if(slide===0){setSlide(carImages.length-1)}else{setSlide(slide-1)}}}><ArrowLeftCircleIcon/></button>
            <button className="text-white" onClick={()=>{if(slide===carImages.length-1){setSlide(0)}else{setSlide(slide+1)}}}><ArrowRightCircleIcon/></button>
            {carImages.map((img, index) => {
                return(
                <img className={`${slide===index ? "":"hidden"}`} src={img.src} alt={img.alt} key={index}></img>)})}
            <div>
                {carImages.map((_, index) => {
                    return <button className="bg-white w-3 h-3 rounded-[50%]" key={index} onClick={()=> setSlide(index)}></button>
                })}
            </div>
        </div>
    )
}

export default Carousel
