import React from 'react';
import {ArrowRightCircleIcon, ArrowLeftCircleIcon} from "@heroicons/react/24/outline"
<meta name="viewport" content="width=device-width, initial-scale=1.0"></meta>

function Carousel(){

    const [slide, setSlide] = React.useState(0);
    var slideInterval;

    function startSlider(){
        console.log(slideInterval)
        if (!slideInterval){
        //slideInterval = setInterval(()=> {if(slide===carImages.length-1){setSlide(0)}else{setSlide(slide+1)}}, 5000);
        }
    }

    function slideLeft(){
        if(slide===0){
            setSlide(carImages.length-1)
        }else{
            setSlide(slide-1)
        };
    }

    function slideRight(){
        if(slide===carImages.length-1){
            setSlide(0)
        }else{
            setSlide(slide+1)
        };
    }

    function stopSlider(){
        clearInterval(slideInterval)
    }

    var carImages=[
        {
            src: require("../Images/rivulet.jpg"),
            alt: "Image1",
        },
        {
            src: require("../Images/structure.png"),
            alt: "Image2",
        },
        {
            src: require("../Images/fastboy.jpg"),
            alt: "Image3",
        },
    ]

    return (
        <div id="carousel" className="relative flex justify-center w-full">
            <button className="w-8 bg-white rounded-[50%] p-[-1] h-fit my-auto text-black" onClick={()=>{slideLeft()}}><ArrowLeftCircleIcon/></button>
            <div className="relative w-full h-screen overflow-hidden">
            <div className="absolute bg-gray-400 w-[20vw] h-full right-0 z-10"></div>
            <div className="absolute bg-gray-400 w-[20vw] h-full left-0 z-10"></div>
            <div className="absolute flex left-[2000px]">
            {carImages.map((img, index) => {
                return(
                <img className="w-[60vw] h-[60vh]" src={img.src} alt={img.alt} key={index}></img>)})}</div></div>
                <button className="w-8 bg-white rounded-[50%] h-fit my-auto text-black" onClick={()=>{slideRight()}}><ArrowRightCircleIcon/></button>
            <div className="absolute flex flex-row gap-2 justify-center bottom-3 right-[auto]">
                {carImages.map((_, index) => {
                    return <button className={`${index===slide ? "bg-cyan-400" : "bg-white"} w-3 h-3 rounded-[50%]`} key={index} onClick={()=> setSlide(index)}></button>
                })}
            </div>
        </div>
    )
}

export default Carousel
