import React from 'react'
import Axios from 'axios'
import {useTable} from 'react-table';

function Databases(){

    const [arrayData, setarrayData] = React.useState([]);
    const tableData = React.useMemo(() => arrayData, []);
    const tableCols = React.useMemo(() => [
        {
            Header: "ID",
            accessor: 0
        },
        {
            Header: "First Name",
            accessor: 1
        },
        {
            Header: "Last Name",
            accessor: 2
        },
        {
            Header: "E-mail",
            accessor: 3
        },
        {
            Header: "Address",
            accessor: 4
        },
        {
            Header: "Phone Number",
            accessor: 5
        },
        {
            Header: "Access Level",
            accessor: 6
        },
        {
            Header: "Password",
            accessor: 7
        },
    ], []);

    const {getTableProps, getTableBodyProps, headerGroups, rows, prepareRow} = useTable({tableCols, tableData});

    React.useEffect(() => {
        try {
            Axios.get("/test").then((response) => {
                setarrayData(response.data.rows)
            })
        } catch (err){
            console.log(err);
        }
    }, [])

    return(
        <div>
            <h1>Databases</h1>
            <div className='table-container'>
                <table {...getTableProps()}>
                    <thead>
                        {headerGroups.map((headerGroup) => {
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map((tableCols) => {
                                    <th {...tableCols.getHeaderProps()}>
                                        {tableCols.render("Header")}
                                    </th>
                                })}
                            </tr>
                        })}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map((row) => {
                            prepareRow(row)
                            return (
                                <tr {...row.getRowProps()}>
                                    {row.cells.map((cell) => {
                                        return (<td {...cell.getCellProps()}>
                                            {cell.render("Cell")}
                                        </td>)
                                    })}
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Databases;
