import "../styles/Database.css";
import React from 'react';
import Axios from 'axios';
import {flexRender, getCoreRowModel, useReactTable} from '@tanstack/react-table';

function Databases(){

     //runs only on first load. Only used to automatically fetch the database data and store it
     React.useEffect(() => {
        try {
            Axios.get("/test").then((response) => {
                console.log(response.data)
                setarrayData(response.data.rows);
            })
        } catch (err){
            console.log(err);
        }
    }, [])
    //useState to store the data obtained from the database
    const [arrayData, setarrayData] = React.useState([]);

    const columns = [
        {
            id: 'FirstName',
            accessorFn: row => `${row[1]}`,
            header: "First Name",
        },
        {
            accessorKey: "2",
            header: "Last Name",
        },
        {
            accessorKey: "3",
            header: "Email Address",
        },
        {
            accessorKey: "4",
            header: "Address",
        },
        {
            accessorKey: "5",
            header: "Phone Number",
        },
        {
            accessorKey: "6",
            header: "Access Level",
        },
        {
            accessorKey: "7",
            header: "Password",
        },
    ]
    const table = useReactTable({
        columns,
        arrayData,
        getCoreRowModel:getCoreRowModel()
    })

    return(
        <div>
            <h1>Databases</h1>
            <div className='table-container'>
                {table.getHeaderGroups().map(headerGroup => <div className = "tr" key={headerGroup.id}>
                    {headerGroup.headers.map(
                        header => <div className="th" key={header.id}>
                            {header.column.columnDef.header}
                        </div>
                    )}
                </div>)}
                {//getRowModel().rows returns an array of rows
                table.getRowModel().rows.map(row => <div className = "tr" key={row.id}>
                {row.getVisibleCells().map(cell => <div className="td" key={cell.id}>
                        {flexRender(
                            cell.column.columnDef.cell,
                            cell.getContext()
                        )}
                    </div>)}
                </div>)
            }
            </div>
        </div>
    )
}

export default Databases;
